mapper.select:
SELECT NVL(COUNT(*), 0)
FROM campingsite 
		
	where  memberIdx=#{memberIdx}

SELECT NVL(COUNT(*), 0)
FROM sitedetail
where  siteNum=#{siteNum}

mapper.sql:
ENABLED= 0 


	and  INSTR(siteName, #{siteKwd}) > 0


	and  siteLocal=#{siteLocal}


	and    Environment=#{siteEnvironment}


	and  CATEGORY=#{siteCategory}




	
		and campingsite.siteNum in (select  distinct siteNum from sitedetail where  #{minFee} <= price and #{maxFee} >= price)
	
	
		and campingsite.siteNum in (select  distinct siteNum from sitedetail where  #{minFee} <= price )
	
	
		and campingsite.siteNum in (select  distinct siteNum from sitedetail where  #{maxFee} >= price)

mapper.select:
SELECT siteNum,siteName,siteLocal,addr1,addr2, CATEGORY,ENVIRONMENT,hitCount,THUMBNAIL, AVGSTAR,introduce FROM campingsite
and memberIdx=#{memberIdx}
ORDER BY siteNum DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

		SELECT detailNum,siteNum,detailName,PRICE,AREA,CONTENT,CAPACITY,floor, nvl(star,0) star
		FROM sitedetail 
        left join (select trunc(avg(score),1) star,detailnum dnum from sitereview  group by detailnum) on dnum=detailnum
		where siteNum=#{siteNum}
		ORDER BY detailNum DESC;

SELECT detailNum,siteNum,detailName,PRICE,AREA,CONTENT,CAPACITY,floor, nvl(star,0) star FROM sitedetail left join (select trunc(avg(score),1) star,detailnum dnum from sitereview group by detailnum) on dnum=detailnum
siteNum=#{siteNum}
ORDER BY detailNum

SELECT SPICNUM fileNum, siteNum, fileName
FROM sitepic
WHERE siteNum = #{siteNum}

SELECT SdPICNUM fileNum, detailNum , fileName
FROM sitedetailPic
WHERE detailNum = #{detailNum}

mapper.update:
	UPDATE campingsite SET siteName=#{siteName},INTrODUCE=#{introduce},
			siteLocal=#{siteLocal},ZIP=#{zip},addr1=#{addr1},addr2=#{addr2},
 		 THUMBNAIL= #{thumbnail},CATEGORY=#{category},
 		 ENVIRONMENT=#{environment},checkIn= #{checkIn},checkOut= #{checkOut},
 		 siteOption= #{siteOption}
	WHERE siteNum = #{siteNum}

UPDATE sitedetail SET
	detailName=#{detailName},PRICE=#{price},
	AREA=#{area},CONTENT=#{content},CAPACITY=#{capacity},floor=#{floor}
WHERE detailNum = #{detailNum}
