mapper.delete:
DELETE FROM sitecart
WHERE memberIdx = #{memberIdx}
AND detailNum = #{detailNum}

mapper.select:
		
		 select memberIdx,detailNum,to_char(STARTDATE,'yyyy-mm-dd') STARTDATE,to_char(ENDDATE,'yyyy-mm-dd') ENDDATE,to_char(REG_DATE,'yyyy-mm-dd') REG_DATE,filename thumbnail
		FROM sitecart 
		left join (select filename,detailNum dnum from sitedetailpic 
        where detailNum = #{detailNum}
        ORDER BY sdpicnum 
		FETCH FIRST 1 ROWS ONLY
        ) on dnum=detailNum
		WHERE detailNum = #{detailNum} and  memberIdx=#{memberIdx}

SELECT NVL(COUNT(*), 0)
FROM sitecart 
where memberIdx=#{memberIdx}

SELECT listNum,to_char(order_date,'yyyy-mm-dd') order_date, memberIdx,totalPrice 
FROM booklist 
WHERE memberIdx=#{memberIdx}

mapper.update:
update sitereview set
	answer=#{answer},anweridx=#{answerIdx}
where booknum={bookNum}

mapper.select:
		select NVL(COUNT(*), 0)
		 from book
         left join booklist on book.listnum=booklist.listnum
		
			
            	memberIdx=#{memberIdx}
			
			
            	detailnum=#{detailNum}

select NVL(COUNT(*), 0)
 from booklist
where memberidx=#{memberIdx}
